package com.notion.core.dataimport com.notion.core.domain.Productimport com.notion.core.helper.DataStateimport kotlinx.coroutines.flow.Flowimport kotlinx.coroutines.flow.flowclass UserRepository(    private val userLocalDataSource: UserLocalDataSource) {    suspend fun getProductList() : Flow<DataState<List<Product>>> = flow {        try {            emit(                DataState.Success(                    userLocalDataSource.getProductList()                )            )        } catch (e : Exception){            userLocalDataSource.getProductList()        }    }}