package com.notion.presentation.chartimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.notion.Rimport com.notion.databinding.ItemGroupBindingimport com.notion.databinding.ItemProductSmallBindingclass GroupListAdapter(private val context: Context) : RecyclerView.Adapter<GroupListAdapter.GroupListAdapterViewHolder>() {    inner class GroupListAdapterViewHolder(private val binding : ItemGroupBinding) : RecyclerView.ViewHolder(binding.root){        fun onBind(pos : Int){            when(pos){                1 -> {                    binding.ivItemGroupMedal.visibility = View.VISIBLE                    binding.tvItemGroupRank.visibility = View.INVISIBLE                    binding.ivItemGroupMedal.setImageDrawable(context.getDrawable(R.drawable.medal1))                }                2 -> {                    binding.ivItemGroupMedal.visibility = View.VISIBLE                    binding.tvItemGroupRank.visibility = View.INVISIBLE                    binding.ivItemGroupMedal.setImageDrawable(context.getDrawable(R.drawable.medal2))                }                3 -> {                    binding.ivItemGroupMedal.visibility = View.VISIBLE                    binding.tvItemGroupRank.visibility = View.INVISIBLE                    binding.ivItemGroupMedal.setImageDrawable(context.getDrawable(R.drawable.medal3))                }                else -> {                    binding.ivItemGroupMedal.visibility = View.INVISIBLE                    binding.tvItemGroupRank.visibility = View.VISIBLE                    binding.tvItemGroupRank.text = pos.toString()                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GroupListAdapterViewHolder {        return GroupListAdapterViewHolder(ItemGroupBinding.inflate(LayoutInflater.from(parent.context) , parent , false))    }    override fun getItemCount(): Int {        return 20    }    override fun onBindViewHolder(holder: GroupListAdapterViewHolder, position: Int) {        holder.onBind(position + 1)    }}