package com.notion.presentation.groupDetailsimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.viewpager2.widget.ViewPager2import com.notion.databinding.FragmentGroupDetailsBindingimport com.notion.presentation.chart.ChartFragmentimport com.notion.utils.NotionFragmentimport dagger.hilt.android.AndroidEntryPoint@AndroidEntryPointclass GroupDetailsFragment : NotionFragment() {    lateinit var binding : FragmentGroupDetailsBinding    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentGroupDetailsBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()        setupViewPager()    }    private fun initView() {        binding.llGroupDetailsChellange.setOnClickListener {            binding.vGroupDetailsTabChellange.visibility = View.VISIBLE            binding.vGroupDetailsTabMember.visibility = View.INVISIBLE            binding.vpGroupDetails.setCurrentItem(0 , true)        }        binding.llGroupDetailsMember.setOnClickListener {            binding.vGroupDetailsTabChellange.visibility = View.INVISIBLE            binding.vGroupDetailsTabMember.visibility = View.VISIBLE            binding.vpGroupDetails.setCurrentItem(1 , true)        }        binding.vpGroupDetails.registerOnPageChangeCallback(object : ViewPager2.OnPageChangeCallback(){            override fun onPageSelected(position: Int) {                when(position){                    0 -> {                        binding.vGroupDetailsTabChellange.visibility = View.VISIBLE                        binding.vGroupDetailsTabMember.visibility = View.INVISIBLE                    }                    1 -> {                        binding.vGroupDetailsTabChellange.visibility = View.INVISIBLE                        binding.vGroupDetailsTabMember.visibility = View.VISIBLE                    }                }            }        })    }    private fun setupViewPager() {        binding.vpGroupDetails.adapter = GroupDetailsViewPagerAdapter(this)    }}