package com.notion.presentation.homeimport android.content.Contextimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.notion.core.domain.Productimport com.notion.databinding.ItemProductSmallBindingimport com.notion.utils.getIconIDclass ProductListAdapter(private val context: Context , private val isShowInventory : Boolean = false ,private val list: List<Product>) : RecyclerView.Adapter<ProductListAdapter.ProductListAdapterViewHolder>() {    inner class ProductListAdapterViewHolder(private val binding : ItemProductSmallBinding) : RecyclerView.ViewHolder(binding.root){        fun bind(product: Product){            if (!isShowInventory)                binding.tvItemProductSmallInventory.visibility = View.GONE            else {                val number = (1 until 10).random()                binding.tvItemProductSmallInventory.text = number.toString()            }            binding.ivItemProductSmallIcon.setImageDrawable(context.getDrawable(getIconID(product.icon)))        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): ProductListAdapterViewHolder {        return ProductListAdapterViewHolder(ItemProductSmallBinding.inflate(LayoutInflater.from(parent.context) , parent , false))    }    override fun getItemCount(): Int {        return list.size    }    override fun onBindViewHolder(holder: ProductListAdapterViewHolder, position: Int) {        holder.bind(list[position])    }}