package com.notion.presentation.memberGroupimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.notion.databinding.ItemMemberGroupBindingclass MemberAdapter : RecyclerView.Adapter<MemberAdapter.MemberAdapterViewHolder>() {    inner class MemberAdapterViewHolder(private val binding : ItemMemberGroupBinding) : RecyclerView.ViewHolder(binding.root){        fun onBind(pos : Int){            binding.tvItemMemberPosition.text = pos.toString()            val numRand = (1 until 150).random()            binding.tvItemMemberLevel.text = "سطح : $numRand"        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MemberAdapterViewHolder {        return MemberAdapterViewHolder(ItemMemberGroupBinding.inflate(LayoutInflater.from(parent.context) , parent , false))    }    override fun getItemCount(): Int {        return 15    }    override fun onBindViewHolder(holder: MemberAdapterViewHolder, position: Int) {        holder.onBind(position + 1)    }}