package com.notion.presentation.memberGroupimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.LinearLayoutManagerimport com.notion.databinding.FragmentMemberGroupBindingimport com.notion.utils.NotionFragmentimport dagger.hilt.android.AndroidEntryPoint@AndroidEntryPointclass MemberGroupFragment : NotionFragment(){    lateinit var binding : FragmentMemberGroupBinding    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentMemberGroupBinding.inflate(inflater , container , false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        binding.rvMemberGroup.layoutManager = LinearLayoutManager(requireContext() , LinearLayoutManager.VERTICAL , false)        binding.rvMemberGroup.adapter = MemberAdapter()    }}