package com.notion.utilsimport androidx.annotation.DrawableResimport com.notion.R@DrawableResfun getIconID(id : Int) : Int{    return when(id){        1 -> R.drawable.ic_shop1        2 -> R.drawable.ic_shop2        3 -> R.drawable.ic_shop3        4 -> R.drawable.ic_shop4        5 -> R.drawable.ic_shop5        6 -> R.drawable.ic_shop6        7 -> R.drawable.ic_shop7        8 -> R.drawable.ic_shop8        9 -> R.drawable.ic_shop9        10 -> R.drawable.ic_shop10        11 -> R.drawable.ic_shop11        else -> R.drawable.ic_shop1    }}